sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
	/* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
	/* Optional GlideRecord */ event) {

	var guests = "";	

	//Event Source
	if(event.sys_id) {
		//get the recipientFirstName attribute from Parm2 JSON.
		var recipient = JSON.parse(event.parm2);
		var guestName = recipient.recipientFirstName;
		var partnerName = recipient.recipientPartner;
		guests = guestName;
		if (partnerName) {
			guests += " & " + partnerName;
		}
	}

	template.print(guests);

})(current, template, email, email_action, event);
